import React, { useState } from "react";
import { Button, Col, Container, Form, Row } from "react-bootstrap";
import axios from "axios";

function HomePage() {
  const [form, setForm] = useState("");
  const [response, setResponse] = useState([]);
  const token = "";
  const [data, setData] = useState({
    windowPrevState: [],
    windowCurrState: [],
    numbers: [],
    avg: 0,
  });
  const handleForm = async (e) => {
    e.preventDefault();
    try {
      console.log(`http://20.244.26.144/test/${form}`);
      const res = await axios.get(`http://localhost:9000/test/${form}`, {
        headers: {
          Authorization: ``,
        },
      });
      setResponse(res.data.numbers);
      const prevState = [...data.windowCurrState];
      const currState = [...response.data.numbers];
      const num = [...response.data.numbers];
      var total = 0;
      for (var i of num) {
        total += i;
      }

      const avg = total / num.length;
      setData({
        windowPrevState: prevState,
        windowCurrState: currState,
        numbers: num,
        avg: avg,
      });
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <Container className="my-4">
      <Row>
        <Col md="5">
          <Form onSubmit={handleForm}>
            <Form.Select value={form} onChange={(e) => setForm(e.target.value)}>
              <option value="">Select an option</option>
              <option value="prime">Prime</option>
              <option value="fibo">Fibonacci</option>
              <option value="rand">Random</option>
              <option value="e">Even</option>
            </Form.Select>
            <Button type="submit" className="mt-3">
              Search
            </Button>
          </Form>
        </Col>
      </Row>
      <Row className="mt-4">
        <Col md="5">
          <p>Reponse</p>
          <pre>{JSON.stringify(response, null, 5)}</pre>
        </Col>
      </Row>
      <Row className="mt-4">
        <Col md="5">
          <p>Reponse</p>
          <pre>{JSON.stringify(data, null, 5)}</pre>
        </Col>
      </Row>
    </Container>
  );
}

export default HomePage;
